mapper.select:
select d.realname as userName,count(1)as bugsNum,d.account from zt_bug a left join zt_product b on a.product = b.id left join zt_project c on a.project = c.id left join zt_user d on a.resolvedBy = d.account where 1=1
and TIMESTAMPDIFF(hour,a.openedDate,now()) > 48 and a.status = 'active' and a.type='codeerror'
and TIMESTAMPDIFF(hour,a.openedDate,a.resolvedDate) > 48
and b.id = #{systemId}
AND c.id in
#{item}
AND c.name like '%'||(select right(YEAR (CURDATE()),2))
group by d.account order by count(1) desc limit 10;

select d.realname as userName,count(1)as bugsNum,d.account from zt_bug a left join zt_product b on a.product = b.id left join zt_project c on a.project = c.id left join zt_user d on a.resolvedBy = d.account where 1=1
and a.status = 'active'
and b.id = #{systemId}
AND c.id in
#{item}
AND c.name like '%'||(select right(YEAR (CURDATE()),2))
group by d.account order by count(1) desc limit 10;

SELECT a.id as id, b.name as projectName, TIMESTAMPDIFF(hour,a.openedDate,now()) as putHours, a.type as type, a.title as title, d.realname as openedBy, e.realname as assignedTo, f.realname as resolvedBy, a.resolution as resolution FROM zt_bug a LEFT JOIN zt_product b ON a.product = b.id LEFT JOIN zt_project c ON a.project = c.id LEFT JOIN zt_user d ON a.openedBy = d.account LEFT JOIN zt_user e ON a.assignedTo = e.account LEFT JOIN zt_user f ON a.resolvedBy = f.account WHERE 1=1 AND a.status = 'active' AND a.type = 'redev'
AND c.id in
#{item}
AND c.name like '%'||(select right(YEAR (CURDATE()),2))
AND b.id = #{systemId}
AND TIMESTAMPDIFF(hour,a.openedDate,now()) > 48
AND d.account = #{account}
ORDER BY id DESC
