mapper.select:
select d.realname as userName,count(1)as bugsNum,d.account from zt_bug a left join zt_product b on a.product = b.id left join zt_project c on a.project = c.id left join zt_user d on a.assignedTo = d.account where 1=1
and TIMESTAMPDIFF(hour,a.openedDate,now()) > 48 and a.status = 'active' and a.type='codeerror'
and TIMESTAMPDIFF(hour,a.openedDate,case when a.resolvedDate is null then now() else a.resolvedDate end ) > 48 and a.status <> 'closed'
and b.id = #{systemId}
AND c.id in
#{item}
AND c.name like concat('%',(select right(YEAR (CURDATE()),2)),'%')
group by d.account order by count(1) desc limit 10;

select d.realname as userName,count(1)as bugsNum,d.account from zt_bug a left join zt_product b on a.product = b.id left join zt_project c on a.project = c.id left join zt_user d on a.assignedTo = d.account where 1=1
and a.status = 'active'
and a.status <> 'closed'
and b.id = #{systemId}
AND c.id in
#{item}
AND c.name like concat('%',(select right(YEAR (CURDATE()),2)),'%')
group by d.account order by count(1) desc limit 10;
