mapper.select:
SELECT a.id as id, b.name as projectName, TIMESTAMPDIFF(hour,a.openedDate,now()) as putHours, a.type as type, a.title as title, d.realname as openedBy, e.realname as assignedTo, f.realname as resolvedBy, a.resolution as resolution FROM zt_bug a LEFT JOIN zt_product b ON a.product = b.id LEFT JOIN zt_project c ON a.project = c.id LEFT JOIN zt_user d ON a.openedBy = d.account LEFT JOIN zt_user e ON a.assignedTo = e.account LEFT JOIN zt_user f ON a.resolvedBy = f.account WHERE 1=1
AND c.id in
#{item}
AND c.name like concat('%',right(YEAR (CURDATE()),2),'%')
AND b.id = #{systemDTO.systemId}
AND a.status = 'active' AND a.type = 'redev' ORDER BY id DESC limit #{page.minNum},#{page.pageSize}

SELECT COUNT(*) FROM zt_bug a LEFT JOIN zt_product b ON a.product = b.id LEFT JOIN zt_project c ON a.project = c.id LEFT JOIN zt_user d ON a.openedBy = d.account LEFT JOIN zt_user e ON a.assignedTo = e.account LEFT JOIN zt_user f ON a.resolvedBy = f.account WHERE 1=1
AND c.id in
#{item}
AND c.name like concat('%',right(YEAR (CURDATE()),2),'%')
AND b.id = #{systemDTO.systemId}
AND a.status = 'active' AND a.type = 'redev'

SELECT a.id as id, b.name as projectName, TIMESTAMPDIFF(hour,a.openedDate,now()) as putHours, a.type as type, a.title as title, d.realname as openedBy, e.realname as assignedTo, f.realname as resolvedBy, a.resolution as resolution FROM zt_bug a LEFT JOIN zt_product b ON a.product = b.id LEFT JOIN zt_project c ON a.project = c.id LEFT JOIN zt_user d ON a.openedBy = d.account LEFT JOIN zt_user e ON a.assignedTo = e.account LEFT JOIN zt_user f ON a.resolvedBy = f.account WHERE 1=1
AND a.story = #{systemDTO.storyId}
AND c.id in
#{item}
AND c.name like concat('%',right(YEAR (CURDATE()),2),'%')
AND b.id = #{systemDTO.systemId}

